function validarEmail(email){let regex=/^[^\s@]+@[^\s@]+\.[^\s@]+$/;return regex.test(email)}function debounce(func,timeout=300){let timer;return(...args)=>{clearTimeout(timer);timer=setTimeout(()=>{func.apply(this,args)},timeout)}}function validateCardNumber(number){number=number.replace(/\s/g,"");if(!/^\d+$/.test(number)){return false}console.log(number);const length=number.length;let sum=0;let double=false;for(let i=length-1;i>=0;i--){let digit=parseInt(number.charAt(i),10);if(double){digit*=2;if(digit>9){digit-=9}}sum+=digit;double=!double}return sum%10===0}function validarCPF(cpf){cpf=cpf.replace(/\D/g,"");if(cpf.length!==11){return false}if(/^(\d)\1+$/.test(cpf)){return false}let soma=0;for(let i=0;i<9;i++){soma+=parseInt(cpf.charAt(i))*(10-i)}let primeiroDigito=11-soma%11;if(primeiroDigito>9){primeiroDigito=0}soma=0;for(let j=0;j<10;j++){soma+=parseInt(cpf.charAt(j))*(11-j)}let segundoDigito=11-soma%11;if(segundoDigito>9){segundoDigito=0}return parseInt(cpf.charAt(9))===primeiroDigito&&parseInt(cpf.charAt(10))===segundoDigito}function validarCNPJ(cnpj){cnpj=cnpj.replace(/\D/g,"");if(cnpj.length!==14){return false}if(/^(\d)\1+$/.test(cnpj)){return false}let tamanho=cnpj.length-2;let numeros=cnpj.substring(0,tamanho);let digitos=cnpj.substring(tamanho);let soma=0;let pos=tamanho-7;for(let i=tamanho;i>=1;i--){soma+=parseInt(numeros.charAt(tamanho-i))*pos--;if(pos<2){pos=9}}let primeiroDigito=soma%11<2?0:11-soma%11;if(parseInt(digitos.charAt(0))!==primeiroDigito){return false}tamanho+=1;numeros=cnpj.substring(0,tamanho);digitos=cnpj.substring(tamanho);soma=0;pos=tamanho-7;for(let j=tamanho;j>=1;j--){soma+=parseInt(numeros.charAt(tamanho-j))*pos--;if(pos<2){pos=9}}let segundoDigito=soma%11<2?0:11-soma%11;return parseInt(digitos.charAt(0))===segundoDigito}