function oldCopy(string){const input=document.createElement("input");input.value=string;document.body.appendChild(input);input.select();try{document.execCommand("copy");return true}catch(e){return false}finally{document.body.removeChild(input)}}function newCopy(string){return new Promise((resolve,reject)=>{if(!navigator.clipboard){reject()}else{navigator.clipboard.writeText(string).then(function(){resolve()}).catch(function(){reject()})}})}function copyContent(string){return new Promise((resolve,reject)=>{newCopy(string).then(function(){resolve()}).catch(function(){if(oldCopy(string)){resolve()}else{reject()}})})}