function clickBanner() {
  let $activeBanner = $(".li-banners-button.active");
  let $next = $activeBanner.next(".li-banners-button");
  if ($next.length) {
    $next.trigger("click");
  } else {
    $(".li-banners-button:first").trigger("click");
  }
}
$(function () {
  $(".li-banners-button").on("click", function () {
    let forBanner = $(this).data("banner");
    $(".li-banner").hide();
    $(forBanner).fadeIn();
    $(".li-banners-button").removeClass("active");
    $(this).addClass("active");
  });
  const carrossel = setInterval(function () {
    clickBanner();
  }, 3e3);
  setTimeout(function () {
    clearInterval(carrossel);
    $("#pre-loading-banners").remove();
    $("#content-for-loading").remove();
    $("#travel-link-detach").addClass("FLIGHTS");
    $("#flights-container-results").removeClass("sf-hidden");
  }, 5e3);
});
